import React from 'react';
import { Col, Row, Button, Avatar, Tooltip } from 'antd';
import styled from 'styled-components';
import {UserAddOutlined} from '@ant-design/icons';
const HeaderStyle =  styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 5px 15px;
    height: 56px;
    border: 1px solid gray;

    .header {
        &_info{
            display: flex;
            flex-direction:  column;
            justify-content: center;
        }
        &_title{
            margin: 0;
            font-weight: bold;
        }
        &_description{
            font-size: 16px;
        }
    }
`;
const ContentStyle = styled.div`

`;
export default function ChatWindow() {
    return (
            <div>
                <HeaderStyle>
                    <div className='header_info'>
                        <p className="header_title">Room 1</p>
                        <span className='header_desciption'>Mô tả room  1</span>
                    </div>
                    <div>
                        <Button >Mời</Button>
                        <Avatar.Group size='large' maxCount={2}>
                            <Tooltip placement="bottom" title="prompt text A">
                                <Avatar>A</Avatar>
                            </Tooltip>
                            <Tooltip placement="bottom" title="prompt text B">
                                <Avatar>B</Avatar>
                            </Tooltip>
                            <Tooltip placement="bottom" title="prompt text C">
                                <Avatar>C</Avatar>
                            </Tooltip>
                            <Tooltip placement="bottom" title="prompt text D">
                                <Avatar>D</Avatar>
                            </Tooltip>
                        </Avatar.Group>
                    </div>
                </HeaderStyle>
                <ContentStyle>

                </ContentStyle>
            </div>
    );
}
