import React from 'react';
import { Col, Row, Button, Avatar, Tooltip, Form, Input } from 'antd';
import styled from 'styled-components';
import {UserAddOutlined} from '@ant-design/icons';
import _Message from './_Message';
const HeaderStyle =  styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 5px 15px;
    height: 56px;
    border: 1px solid gray;

    .header {
        &_info{
            display: flex;
            flex-direction:  column;
            justify-content: center;
        }
        &_title{
            margin: 0;
            font-weight: bold;
        }
        &_description{
            font-size: 16px;
        }
    }
    .button_group{
        display: flex;
        justify-content: space-bettween;
        align-items: center;

        .ant-btn.button_invite {
            margin-right: 5px;
        }
    }
    
`;
const ContentStyle = styled.div`

`;
const MessageListStyle = styled.div`

`;
export default function ChatWindow() {
    return (
            <div>
                <HeaderStyle>
                    <div className='header_info'>
                        <p className="header_title">Room 1</p>
                        <span className='header_desciption'>Mô tả room  1</span>
                    </div>
                    <div className='button_group'>
                        <Button className="button_invite" type='text' icon={<UserAddOutlined/>} >Mời</Button>
                        <Avatar.Group size='large' maxCount={2}>
                            <Tooltip placement="bottom" title="prompt text A">
                                <Avatar>A</Avatar>
                            </Tooltip>
                            <Tooltip placement="bottom" title="prompt text B">
                                <Avatar>B</Avatar>
                            </Tooltip>
                            <Tooltip placement="bottom" title="prompt text C">
                                <Avatar>C</Avatar>
                            </Tooltip>
                            <Tooltip placement="bottom" title="prompt text D">
                                <Avatar>D</Avatar>
                            </Tooltip>
                        </Avatar.Group>
                    </div>
                </HeaderStyle>
                <ContentStyle>
                    <MessageListStyle>
                        {/* <_Message text='text 1' photoURL={null} createdAt={123456} displayName='Tùng'></_Message>
                        <_Message text='text 2' photoURL={null} createdAt={123456} displayName='nAM'></_Message>
                        <_Message text='text 3' photoURL={null} createdAt={123456} displayName='sƠN'></_Message>
                        <_Message text='text 4' photoURL={null} createdAt={123456} displayName='Quốc'></_Message>
                        <_Message text='text 5' photoURL={null} createdAt={123456} displayName='An'></_Message> */}
                    </MessageListStyle>
                    <Form>
                        <Form.Item
                            label="Username"
                            name="username"
                        >
                            <Input />
                        </Form.Item>
                        <Button type="primary" htmlType="submit">
                        Submit
                    </Button>
                    </Form>
                </ContentStyle>
            </div>
    );
}
