import React from 'react';
import { Row, Col, Button, Typography } from 'antd';
import firebase, { auth } from '../../firebase/config';

const {Title} = Typography;
const fbProvider = new firebase.auth.FacebookAuthProvider();

export default function Login() {
    const handleFbLogin = () =>{
        try {
            if(auth.signInWithPopup(fbProvider)){
                auth.signInWithPopup(fbProvider);
                console.log('auth.signInWithPopup(fbProvider)', auth.signInWithPopup(fbProvider));
            }   
        } catch (error) {
            console.log(error.message)
        }
        
    };
    return (
        <div>
            <Row justify="center">
                <Title level={3}>Chat for fun</Title>
                <Col span={8}>
                    <Button>Đăng nhập bằng GOOGLE</Button>
                </Col>
                <Col>
                    <Title></Title>
                    <Button onClick={handleFbLogin}>Đăng nhập bằng FACEBOOK</Button>
                </Col>
      </Row>
        </div>
    );
}
