import React, { useContext, useMemo } from 'react';
import {Row, Col, Button ,Collapse, Typography} from 'antd';
import styled from 'styled-components';
import {PlusSquareOutlined} from '@ant-design/icons'
import useFireStore from '../../hooks/useFireStore';
import { AuthContext } from '../../Context/AuthProvider';

const { Panel } = Collapse;
const PanelStyle = styled(Panel)`
 &&&{
    .ant-collapse-header{
        color: white;
    }
    a.ant-typography, .ant-typography a {
        color: white;
    }
    .ant-collapse-content-box{
        padding: 15px 40px;
        color: white;
    }
    .addroom{
        color: white;
        padding: 0;
    }
`;
const TypographyStyle = styled(Typography.Link)`
    display: block;

`;


export default function RoomList(props) {

    const {user: {uid}} = useContext(AuthContext); 
    console.log('=== curent uid', uid);
    
    /* 
    name: room-name,
    description: 'mo ta',
    member: [uid1, uid3, uid3,...]
     */
    const roomsCondition = useMemo(()=>{
        return {
            fieldName: 'members',
            operator: 'array-contains',
            compareValue: uid
        }
    },[uid]);
    const rooms = useFireStore('rooms', roomsCondition);
    console.log('=========== rooms', rooms);

    return (
        <Collapse ghost defaultActiveKey={['1']}>
            <PanelStyle header="Danh sách các phòng" key="1">
                {
                    rooms.map(room => <TypographyStyle key={room.id}>{room.name}</TypographyStyle>)
                }
                <Button type='text' icon={<PlusSquareOutlined/>} className='addroom'>Thêm phòng</Button>
            </PanelStyle>
            
        </Collapse>
    );
}
